import Control.Monad
import Text.Parsec
import Data.Array
import Data.List
import Data.Foldable (toList)
import Control.Applicative hiding ((<|>),many)
import Data.Array.Base (unsafeAt)

type Parser = Parsec String ()

item :: Parser (String,String)
item = do
  val <- many1 $ noneOf "\t"
  tab
  key <- many1 $ char '0' <|> char '1'
  eof
  return (key,val)

replaceReturn :: String -> String
replaceReturn s = if s == "[newline]" then "\n" else s

alphabet :: [String] -> [(String,String)]
alphabet input = let
  foo s = parse item "" s
  output = map foo input
  output' = map (either (const ("","")) id) output
  output'' = (fmap . fmap) replaceReturn output'
  in output''

huff :: (String,String) -> Parser String
huff (key,val) = do
  k <- try $ string key
  return val

huffman :: [(String,String)] -> Parser [String]
huffman alphabet = let
  parsers = map huff alphabet
  combined = foldl' (<|>) (head parsers) (tail parsers)
  in do
    c <- many combined
    eof
    return c
  
decoding encodings encodedstring = let
  p = huffman $ alphabet encodings
  parsed = either (const []) id $ parse p "" encodedstring
  in concat parsed



